import { useState, useEffect } from "react";

export function Bar ({setTasks, tasks  ,element, index,}) {
    const [done, setDone] = useState(false);
    const [edit, setEdit] = useState(false);
    const [inputValue, setInputValue] = useState('');

    useEffect(() => {
        setInputValue(element);
      }, []);

    function handleCheckboxChange(event) {
        if(event.target.checked)
            setDone(true);
        else
            setDone(false);
    }

    function handleChange(event) {
        setInputValue(event.target.value);

    }

      function handleEdit(index) {
        setEdit(true);
    }


    function handleKeyPress(event){
        if (event.key === 'Enter' && inputValue!=="") {

            setTasks((prevValues) => {
                const updatedValues = [...prevValues];
                updatedValues[index] = inputValue;
                setEdit(false);
                return updatedValues;

            });
        }
    }


  function handleDelete(index) {

    const updatedValues = [...tasks];
    updatedValues.splice(index, 1);
    setTasks(updatedValues);
  }

    if(edit)
    {        
        return(
            <div key={index} className='bar'>

                <div>        
                <input   className="checkbox" type='text' value={inputValue} onChange={handleChange} onKeyDown={handleKeyPress} autoFocus />                
                </div>
                <div>
                <i id='edit' onClick={() => handleEdit(index)} className='fa fa-edit edit'></i>
                <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
                </div>

          </div>
            );

    }
    else
    {
        return(
            <div key={index} className='bar'>

                <div>        
                <input   className="cbox" type='checkbox' onChange={handleCheckboxChange} />
                <span className= {done? 'strike':'normal'} >{inputValue}</span>
                </div>
                <div>
                <i id='edit' onClick={() => handleEdit(index)} className='fa fa-edit edit'></i>
                <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
                </div>

          </div>
            );

    }
}
