    divToBeDisplayed =<div><input   className="cbox" type='checkbox' onChange={handleCheckboxChange} /> <span className= {done? 'strike':'normal'} >{task}</span> </div>

  }




  return(
    <div className='todoTask'>
      <div>
        <input    type='checkbox' onChange={strike} /> 
        <span className= {done? 'strike':'normal'} >{element}</span> 
      </div>

      <div>
        <i id='edit'  className='fa fa-edit edit' onClick={() => handleEdit(index)}></i>
