import { useDispatch, useSelector } from "react-redux";
import {useState} from "react"


export function ToDoTask ({element, key}) {
  debugger
  console.log("element:", element)
  console.log("key:", key)
  const dispatch=useDispatch()
  const [done, setDone] = useState(false)

  function strike()
  {
    setDone(true);
  }

  return(
    <div className='todoTask'>
      <div>
        <input   className={done? 'strkie' : 'nom'} type="checkbox"  onClick={strike}/> 
        <span className= 'normal'>{element}</span> 
      </div>               
      <div>
        {/* <i id='edit'  className='fa fa-edit edit' onClick={() => dispatch(editTask())}></i>
        <i id='del' className="fa-solid fa-trash-can" onClick={() => dispatch(deleteTask())}></i> */}
      </div>    
  </div>
    );
  }
