import { useState } from "react";

export function Bar ({setEditValue, handleEditChange, editValue, tasks , isChecked ,element, index, handleEdit, handleDelete}) {
    setEditValue(tasks[index]);
    const [done, setDone] = useState(false);
    const [edit, setEdit] = useState(false);

    function handleCheckboxChange(event) {
        if(event.target.checked)
        {
            setDone(true);
        }
        else
        {
            setDone(false);
        }

    }

    function handleKeyPress(){
        if (event.key === 'Enter' && inputValue!=="") {

            setTa
          setTasks((prevValues) => [...prevValues, inputValue.trim()]);
        }
      }

    if(edit)
    {

        return(
            <div key={index} className='bar'>

                <div>

                <input   className="cbox" type='text' value={editValue} onChange={handleEditChange} onKeyDown={handleKeyPress(index)} autoFocus />

                </div>
                <div>
                <i id='edit' onClick={setEdit(true)} className='fa fa-edit edit'></i>
                <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
                </div>

          </div>
            );

    }
    else
    {
        return(
            <div key={index} className='bar'>

                <div>

                <input   className="cbox" type='checkbox' onChange={handleCheckboxChange} />
                <span className= {done? 'strike':'normal'} >{element}</span>
                </div>
                <div>
                <i id='edit' onClick={() => handleEdit(index)} className='fa fa-edit edit'></i>
                <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
                </div>

          </div>
            );

    }
}
