import { useDispatch, useSelector } from "react-redux";
import {useState} from "react"


export function ToDoTask ({element, key}) {
  debugger
  console.log("element:", element)
  console.log("key:", key)
  const dispatch=useDispatch()
  const [done, setDone] = useState(false)



  function strike(event)
  {
    if(event.target.checked)
      setDone(true);
    else
      setDone(false);  }

  function handleDelete(key)
  {
    const tasks = useSelector(state => state.list.tasks);

    tasks.splice(key,1);
  }



  return(
    <div className='todoTask'>
      <div>
        <input    type='checkbox' onChange={strike} /> 
        <span className= {done? 'strike':'normal'} >{element}</span> 
      </div>

      <div>
        {/* <i id='edit'  className='fa fa-edit edit' onClick={() => dispatch(editTask())}></i> */}
        <i id='del' className="fa-solid fa-trash-can" onClick={handleDelete(key)}></i>
      </div>    
  </div>
    );
  }
