import axios from "axios";
import { takeEvery, call, put, all } from 'redux-saga/effects';

function* createTodoSaga(action) {
  try {
    const response = yield call(
      axios.post,
      'https://crudcrud.com/api/bd1d8aff47e543d3b8547d571daa66f1',
      action.payload,
      {
        headers: {
          Authorization: 'Bearer your_token_here',
          'Content-Type': 'application/json',
        },
      }
    );
    yield put({ type: 'ADD_TODO_SUCCESS', payload: response.data });
  } catch (error) {
    yield put({ type: 'ADD_TODO_FAILURE', error: error.message });
  }
}

function* watchAddTodo() {
  yield takeEvery('ADD_TODO', createTodoSaga);
}

export default function* rootSaga() {
  yield all([watchAddTodo()]);
}
