import { combineReducers } from "redux"
import {ADD_TODO} from '../actions/index'

const initialState = {
    tasks: [""],
}

function list(state = initialState, action) {

    switch (action.type) {
        case ADD_TODO:
            return {
                tasks: [...state.tasks, action.payload]
            };
        case STRIKE_THROUGH:

        default:
            return state;

    }

}

const rootReducer = combineReducers({
    list,
});

export default rootReducer;
