import { combineReducers } from "redux"
import {ADD_TODO} from '../actions/index'

const initialState = {
    tasks: [],
}

function taskReducer(state = initialState, action) {
    console.log(action);
    console.log(state);
    switch (action.type) {
        case ADD_TODO:
            return [
                ...state.tasks, [...state.tasks],
            ];
            default:
                return state;

    }
    console.log(state);
}

const rootReducer = combineReducers({
    tasks:taskReducer,

});

export default rootReducer;
