import React, {  useState } from 'react';
import './App.css'
import '@fortawesome/fontawesome-free/css/all.min.css';
import {RenderApp} from './Components/RenderApp/index'




function App() {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [completedTasks, setCompletedTasks] = useState([])
  const [editValue, setEditValue] = useState('');


  function handleChange(event) {
    setInputValue(event.target.value);

  }



  function handleDelete(index) {
    setTasks( (prevValues) => {
      const updatedValues = [...prevValues];
      updatedValues.splice(index, 1);
      return updatedValues;
    }

    );

  }

  function handleEdit(index) {
    setEdit(true);    

  }


  function handleKeyPress(event) {
    if (event.key === 'Enter' && inputValue!=="") {
      console.log("Enter pressed");
      setTasks((prevValues) => [...prevValues, inputValue.trim()]);
      setInputValue('');
    }
  }

  function handleCheckboxChange(index) {
    setCompletedTasks( (prevCompletedTasks) => {
      const updatedCompletedTasks = [...prevCompletedTasks];
      updatedCompletedTasks[index] =! updatedCompletedTasks[index];
      return updatedCompletedTasks;
    }

    )
  }





  return (
    <RenderApp handleChange={handleChange} setTasks={setTasks} setEditValue={setEditValue} editValue={editValue}    tasks={tasks} inputValue={inputValue} handleKeyPress={handleKeyPress} handleDelete={handleDelete} handleEdit= {handleEdit} />


  );

}

export default App;
