import { useState } from "react";

export function Bar ({setEditValue, handleEditChange, editValue,  handleChange, tasks, edit , isChecked ,element, index, applyStrikeThrough, handleEdit, handleDelete}) {
    setEditValue(tasks[index]);
    const [done, setDone] = useState(false);

    function handleCheckboxChange(event) {
        if(event.target.checked)
        {
            setDone(true);
        }
        else
        {
            setDone(false);
        }

    }
    if(edit)
    {

        return(
            <div key={index} className='bar'>

                <div>

                <input   className="cbox" type='text' value={editValue} onChange={handleEditChange} autoFocus />

                </div>
                <div>
                <i id='edit' onClick={() => handleEdit(index)} className='fa fa-edit edit'></i>
                <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
                </div>

          </div>
            );

    }
    else
    {
        return(
            <div key={index} className='bar'>

                <div>

                <input   className="cbox" type='checkbox' onChange={handleCheckboxChange} />
                <span className= {setDone? 'strike':'normal'} >{element}</span>
                </div>
                <div>
                <i id='edit' onClick={() => handleEdit(index)} className='fa fa-edit edit'></i>
                <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
                </div>

          </div>
            );

    }
}
