import { useState } from "react";

export function Bar ({isChecked ,element, index, applyStrikeThrough, handleEdit, handleDelete}) {
    const [strike, setStrike] = useState(false);
    setStrike(isChecked);

    if(strike)
    {
        return(
            <div key={index} className='bar'>
                <div>

                <input   className="cbox" type='checkbox' onChange={applyStrikeThrough} />
                <span className= {isChecked?'strike':'normal'} >{element}</span>
                </div>
                <div>
                <i id='edit' onClick={() => handleEdit(index)} className='fa fa-edit edit'></i>
                <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
                </div>

          </div>
            );

    }
    else
    {
        return(
            <div key={index} className='bar'>
                <div>

                <input   className="cbox" type='checkbox' onChange={applyStrikeThrough} />
                <span className= {isChecked? 'strike':'normal'} >{element}</span>
                </div>
                <div>
                <i id='edit' onClick={() => handleEdit(index)} className='fa fa-edit edit'></i>
                <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
                </div>

          </div>
            );

    }

    return(
    <div key={index} className='bar'>
        <div>

        <input   className="cbox" type='checkbox' onChange={applyStrikeThrough} />
        <span className= {isChecked? 'strike':'normal'} >{element}</span>
        </div>
        <div>
        <i id='edit' onClick={() => handleEdit(index)} className='fa fa-edit edit'></i>
        <i id='del' onClick={() => handleDelete(index)}  className="fa-solid fa-trash-can"></i>
        </div>

  </div>
    );
}
