import { combineReducers } from "redux"
import {ADD_TODO} from '../actions/index'
import { STRIKE_THROUGH } from "../actions/index";
const initialState = {
    tasks: [],
}

function list(state = initialState, action) {

    switch (action.type) {
        case ADD_TODO:
        {
            const obj = { task: action.payload, strike: action.strike};

            return {
                ...state, tasks: [...state.tasks, obj],
            };

        }            
        case STRIKE_THROUGH:
        {
            action
            tasks[action.item]



        }

        default:
            return state;

    }

}

const rootReducer = combineReducers({
    list,
});

export default rootReducer;
